var btnThumb = document.getElementsByClassName("btn_thumb");
select.addEventListener("change", checkThumb);
  selectRetreat.addEventListener("change", checkThumb);

function checkThumb () { 
  if (select.value !== select[0].innerHTML && selectRetreat.value !== selectRetreat[0].innerHTML) {      
      $(".btn_thumb").removeClass( "disableBtn" );    
  }
  else{
     
      $(".btn_thumb").addClass( "disableBtn" );
  }
}


// Reset selects
function resetPlay () {
  select.value = select[0].innerHTML;
  selectRetreat.value = selectRetreat[0].innerHTML;
  $(".btn_thumb").addClass( "disableBtn" );
}


// Rotate flip card
function rotate (num) {
  var flipCardInner = document.getElementsByClassName("flip-card-inner"+num)[0];
  if (flipCardInner.classList.contains("rotateFlippingCard")==false) {
      flipCardInner.classList.add("rotateFlippingCard");
    }
    else{
      flipCardInner.classList.remove("rotateFlippingCard")
    }
}


